CREATE DEFINER=`root`@`localhost` PROCEDURE `proc_category`(
	IN in_sub_category_id BIGINT(20)
)
BEGIN

	select tc.category_id as category_id, tc.category_name as category_name,
		tsc.category_id as sub_category_id, tsc.category_name as sub_category_name
		from tbl_category tc, tbl_category tsc
		where tsc.parent_id = tc.category_id
		and tc.parent_id IS NULL
		and tsc.parent_id IS NOT NULL
		and tc.is_deleted='N'
		and tsc.is_deleted='N' 
		order by tsc.category_name;

	IF in_sub_category_id IS NOT NULL THEN 

	select ti.item_id, ti.item_code, ti.item_name,
				tc.category_id as category_id, tc.category_name as category_name,
				tsc.category_id as sub_category_id, tsc.category_name as sub_category_name
				from tbl_item ti, tbl_category tc, tbl_category tsc
				where ti.category_id = tsc.category_id
				and tc.parent_id IS NULL
				and tsc.parent_id IS NOT NULL
				and tsc.parent_id = tc.category_id
				and tc.is_deleted='N'
				and tsc.is_deleted='N' 
				and tsc.category_id = in_sub_category_id
				order by ti.item_name;


	END IF;
END ;;
